WHENEVER OSERROR EXIT FAILURE ROLLBACK

SET DEFINE OFF;

DECLARE
	FUNCTION MissingTable(
		in_schema 		IN all_tables.owner%TYPE,
		in_table_name 	IN all_tables.table_name%TYPE
	) RETURN BOOLEAN 
	AS
		v_cnt	NUMBER(10);
	BEGIN
		SELECT count(*) 
		  INTO v_cnt
		  FROM all_tables
		 WHERE table_name = UPPER(in_table_name)
		   AND owner = UPPER(in_schema);

		RETURN v_cnt = 0;
	END;	
BEGIN
	IF NOT MissingTable('CSR','ROBECOSAM_FACT_TYPE') THEN
		EXECUTE IMMEDIATE 'DROP TABLE csr.robecosam_fact_type CASCADE CONSTRAINTS';
	END IF;
	
	EXECUTE IMMEDIATE '
		CREATE TABLE csr.robecosam_fact_type (
			fact_id 		VARCHAR2(255), 
			type 			VARCHAR2(255), 
			idx 			NUMBER(10), 
			enumeration 	VARCHAR2(255)
		)';
END;
