DECLARE
	FEATURE_NOT_ENABLED EXCEPTION;
	PRAGMA EXCEPTION_INIT(FEATURE_NOT_ENABLED, -439);
    POLICY_ALREADY_EXISTS EXCEPTION;
    PRAGMA EXCEPTION_INIT(POLICY_ALREADY_EXISTS, -28101);

	FUNCTION MissingTable(
		in_schema		IN all_tables.owner%TYPE,
		in_table_name	IN all_tables.table_name%TYPE
	) RETURN BOOLEAN 
	AS
		v_cnt	NUMBER(10);
	BEGIN
		SELECT count(*) 
		  INTO v_cnt
		  FROM all_tables
		 WHERE table_name = UPPER(in_table_name)
		   AND owner = UPPER(in_schema); 

		RETURN v_cnt = 0;
	END;

	FUNCTION MissingFk(
		in_schema			IN all_constraints.owner%TYPE,
		in_table_name		IN all_constraints.table_name%TYPE,
		in_constraint_name	IN all_constraints.constraint_name%TYPE
	) RETURN BOOLEAN 
	AS
		v_cnt	NUMBER(10);
	BEGIN
		SELECT count(*) 
		  INTO v_cnt
		  FROM all_constraints
		 WHERE constraint_name = UPPER(in_constraint_name)
		   AND table_name = UPPER(in_table_name)
		   AND owner = UPPER(in_schema); 
   
		RETURN v_cnt = 0;
	END;

	FUNCTION MissingPol(
		in_schema 		IN all_policies.object_owner%TYPE,
		in_table_name 	IN all_policies.object_name%TYPE,
		in_policy_name 	IN all_policies.policy_name%TYPE
	) RETURN BOOLEAN 
	AS
		v_cnt	NUMBER(10);
	BEGIN
		SELECT count(*) 
		  INTO v_cnt
		  FROM all_policies
		 WHERE object_owner = UPPER(in_schema)
		   AND object_name = UPPER(in_table_name)
		   AND policy_name = UPPER(in_policy_name); 
		   
		RETURN v_cnt = 0;
	END;
BEGIN
	-- TABLE
	IF MissingTable('CSR','SECTION_FACT_ENUM') THEN
		EXECUTE IMMEDIATE '
			CREATE TABLE csr.section_fact_enum(
				app_sid				NUMBER(10)		DEFAULT SYS_CONTEXT(''SECURITY'',''APP'') NOT NULL,
				section_sid			NUMBER(10)		NOT NULL,
				fact_id				VARCHAR2(255)	NOT NULL,
				idx					NUMBER (10)		NOT NULL,
				enumeration			VARCHAR2 (255),
				CONSTRAINT pk_section_fact_enum PRIMARY KEY (app_sid, section_sid, fact_id, idx)
		)';
	END IF;

	-- FK
	IF MissingFk('CSR', 'SECTION_FACT_ENUM', 'FK_SEC_FACT_ENUM_SEC_FACT') THEN
		EXECUTE IMMEDIATE '
			ALTER TABLE csr.section_fact_enum
			  ADD CONSTRAINT fk_sec_fact_enum_sec_fact FOREIGN KEY (app_sid, section_sid, fact_id)
				  REFERENCES csr.section_fact(app_sid, section_sid, fact_id)';
	END IF;
	
	-- RLS
	IF MissingPol('CSR', 'SECTION_FACT_ENUM', 'SECTION_FACT_ENUM_POLICY') THEN
		BEGIN
			dbms_rls.add_policy(
				object_schema   => 'CSR',
				object_name     => 'SECTION_FACT_ENUM',
				policy_name     => 'SECTION_FACT_ENUM_POLICY',
				function_schema => 'CSR',
				policy_function => 'appSidCheck',
				statement_types => 'select, insert, update, delete',
				update_check	=> true,
				policy_type     => dbms_rls.context_sensitive);
		EXCEPTION
			WHEN POLICY_ALREADY_EXISTS THEN
				DBMS_OUTPUT.PUT_LINE('Policy exists');
			WHEN FEATURE_NOT_ENABLED THEN
				DBMS_OUTPUT.PUT_LINE('RLS policies not applied as feature not enabled');
		END;
	END IF;
 END;
 /

BEGIN
	SELECT sf.app_sid, sf.section_sid, sf.fact_id, rf.idx, rf.enumeration
	    FROM csr.section_fact sf
		JOIN csr.robecosam_fact_type rf on sf.fact_id = rf.fact_id
	   WHERE NOT EXISTS (
		SELECT 1
		  FROM csr.section_fact_enum
		 WHERE section_sid = sf.section_sid
		   AND fact_id = sf.fact_id
		   AND idx = rf.idx
		);
	   
	EXECUTE IMMEDIATE 'DROP TABLE csr.robecosam_fact_type CASCADE CONSTRAINTS';
END;
/ 
 
