DECLARE
	v_longitude		NUMBER(24, 10);
	v_latitude		NUMBER(24, 10);
BEGIN	
	FOR r in ( 
		SELECT l.location_type_id, l.name, l.description, l.longitude, l.latitude, l.country, l.is_approved
		  FROM csr.location l
		  JOIN csr.temp_location tl on l.name = tl.name
		 WHERE l.longitude IS NULL AND l.latitude IS NULL
	)
	LOOP
		SELECT longitude, latitude
		  INTO v_longitude, v_latitude
		  FROM csr.temp_location
		 WHERE name = r.name;

		UPDATE csr.location
		   SET longitude = v_longitude,
			   latitude = v_latitude
		 WHERE name = r.name;		 
	END LOOP;

	FOR r in ( 
		SELECT tl.location_type_id, tl.name, tl.description, tl.longitude, tl.latitude, tl.country, tl.is_approved
		  FROM csr.temp_location tl
		 WHERE tl.name NOT IN (SELECT name FROM csr.location)
	)
	LOOP
		 VALUES (csr.location_id_seq.nextval, r.location_type_id, r.name ,r.description, r.longitude, r.latitude, r.country, r.is_approved);
	END LOOP;
END;
